mapper.insert:
INSERT INTO LEASELOAN_OPTION(
	LEASELOAN_NO, LEASELOAN_ID, LEASELOAN_RPAY_TYPE, LEASELOAN_RPAY_TYPE_NM, LEASELOAN_LEND_RATE_TYPE, 
	LEASELOAN_LEND_RATE_TYPE_NM, LEASELOAN_LEND_RATE_MIN, LEASELOAN_LEND_RATE_MAX, LEASELOAN_LEND_RATE_AVG
) VALUES(
	#{leaseNo}, #{leaseId}, #{rpayType}, #{rpayTypeNm}, #{lendRateType}, 
	#{lendRateTypeNm}, #{lendRateMin}, #{lendRateMax}, #{lendRateAvg}
)

mapper.select:
SELECT COUNT(*) FROM (SELECT l.leaseloan_id, l.leaseloan_kor_co_nm, l.leaseloan_fin_prdt_nm, l.leaseloan_join_way, l.leaseloan_loan_inci_expn, l.leaseloan_erly_rpay_fee, l.leaseloan_dly_rate, l.leaseloan_loan_lmt, o.leaseloan_rpay_type_nm, o.leaseloan_lend_rate_type_nm, o.leaseloan_lend_rate_min, o.leaseloan_lend_rate_max, o.leaseloan_lend_rate_avg FROM LEASELOAN l JOIN LEASELOAN_OPTION o ON l.leaseloan_id = o.leaseloan_id JOIN (SELECT leaseloan_id, MAX(leaseloan_lend_rate_max) MAX_RATE FROM LEASELOAN_OPTION GROUP BY leaseloan_id) C ON C.leaseloan_id = l.leaseloan_id AND c.MAX_RATE = o.leaseloan_lend_rate_max
WHERE l.leaseloan_kor_co_nm LIKE '%'||#{korCoNm}||'%'
)
